import { View, Text, Image } from "react-native";
import React from "react";
import {
  Add,
  AddToCart,
  AddToCartText,
  Description,
  Info,
  Item,
  Name,
  Price,
} from "./MenuItem.styles";

const MenuItem = ({ item }) => {
  return (
    <Item style={{ flex: 1 / 2 }}>
      <Image
        source={{
          uri: `http://localhost:5000/uploads/${item.images[0]}`,
          width: "100%",
          height: 150,
        }}
        style={{ borderRadius: 20 }}
      />
      <Info>
        <Name>{item.name}</Name>
        <Description>{item.description} </Description>
      </Info>
      <Price>{item.price}</Price>
      <AddToCart>
        <AddToCartText>Add</AddToCartText>
      </AddToCart>
    </Item>
  );
};

export default MenuItem;
