import { View, Text, Image, Pressable } from "react-native";
import  { useState, useEffect} from "react";
import {
  Checkout,
  CheckoutText,
  Description,
  Item,
  ItemInfo,
  Items,
  Minus,
  Name,
  PaymentDiscount,
  PaymentInfo,
  PaymentPrice,
  Plus,
  Price,
  Quantity,
  QuantityText,
  Separate,
} from "./CartScreen.styles";
import { AntDesign } from "@expo/vector-icons";
import Header from "../../components/Header";
import useAxiosPrivate from " ../../hooks/useAxiosPrivate";

const CartScreen = () => {
  const axiosPrivate = useAxiosPrivate();




  return (
    <>
      <Header />
      <Items>
        <Item
          style={{
            shadowColor: "#000",
            shadowOffset: {
              width: 0,
              height: 5,
            },
            shadowOpacity: 0.34,
            shadowRadius: 6.27,

            elevation: 10,
          }}
        >
          <Image
            source={{
              uri: "https://picsum.photos/200",
              width: 80,
              height: 80,
            }}
            style={{ borderRadius: 10, marginRight: 10 }}
          />
          <ItemInfo>
            <Name>Pizza</Name>
            <Description>Good Pizza</Description>
            <Price>50DH</Price>
            <Quantity>
              <Pressable>
                <AntDesign name="minuscircleo" size={24} color="orange" />
              </Pressable>
              <QuantityText>04</QuantityText>
              <Pressable>
                {/* <Plus style={{ borderRadius: 25 / 2 }}>+</Plus> */}
                <AntDesign name="pluscircle" size={24} color="orange" />
              </Pressable>
            </Quantity>
          </ItemInfo>
        </Item>
      </Items>
      <Separate />
      <PaymentInfo>
        <PaymentDiscount>
          <Text>Discount</Text>
          <Text>30%</Text>
        </PaymentDiscount>
        <PaymentPrice>
          <Text>Total Price</Text>
          <Text>400 MAD</Text>
        </PaymentPrice>
      </PaymentInfo>
      <Checkout>
        <CheckoutText>Checkout</CheckoutText>
      </Checkout>
    </>
  );
};

export default CartScreen;
