import { EvilIcons, Ionicons } from "@expo/vector-icons";
import React, { useState } from "react";
import {
  FlatList,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableWithoutFeedback,
  View,
} from "react-native";
import Header from "../../components/Header";
import Navbar from "../../components/Navbar";
import PopularRestaurants from "../../components/PopularRestaurants";
import globalStyles from "../../styles/global";
const HomeScreen = ({ navigation }) => {
  const [search, setSearch] = useState("");
  const [data, setData] = useState([
    { key: 1, image: "https://picsum.photos/200", name: "name", rating: 4 },
    { key: 2, image: "https://picsum.photos/200", name: "name", rating: 2 },
    { key: 3, image: "https://picsum.photos/200", name: "name", rating: 3 },
    { key: 4, image: "https://picsum.photos/200", name: "name", rating: 4 },
    { key: 5, image: "https://picsum.photos/200", name: "name", rating: 5 },
    { key: 6, image: "https://picsum.photos/200", name: "name", rating: 1 },
    { key: 7, image: "https://picsum.photos/200", name: "name", rating: 1 },
    { key: 8, image: "https://picsum.photos/200", name: "name", rating: 4 },
  ]);

  const renderItem = ({ item }) => <PopularRestaurants item={item} />;

  return (
    <>
      <Header />
      <ScrollView style={styles.container} showsVerticalScrollIndicator={false}>
        <View style={styles.search}>
          <EvilIcons
            name="search"
            size={20}
            color="black"
            style={{ marginRight: 5 }}
          />
          <TextInput
            placeholder="Find your food"
            style={styles.input}
            onChangeText={(value) => setSearch(value)}
          />
          <Ionicons
            name="filter-outline"
            size={20}
            color="black"
            style={{ marginLeft: "auto" }}
          />
        </View>
        <View style={styles.heading}>
          <Text style={styles.headingText}>Nearby Place</Text>
          <TouchableWithoutFeedback>
            <Text style={styles.headingLink}>See All</Text>
          </TouchableWithoutFeedback>
        </View>
        <View style={styles.nearbyPlace}>
          <View style={styles.nearbyPlaceItem}>
            <Image
              source={{
                uri: "https://picsum.photos/200",
                width: 60,
                height: 60,
              }}
              style={{ borderRadius: 10, marginRight: 10 }}
            />
            <View>
              <Text>Name</Text>
              <Text>Address</Text>
            </View>
          </View>
          <View style={styles.nearbyPlaceItem}>
            <Image
              source={{
                uri: "https://picsum.photos/200",
                width: 60,
                height: 60,
              }}
              style={{ borderRadius: 10, marginRight: 10 }}
            />
            <View>
              <Text>Name</Text>
              <Text>Address</Text>
            </View>
          </View>
          <View style={styles.nearbyPlaceItem}>
            <Image
              source={{
                uri: "https://picsum.photos/200",
                width: 60,
                height: 60,
              }}
              style={{ borderRadius: 10, marginRight: 10 }}
            />
            <View>
              <Text>Name</Text>
              <Text>Address</Text>
            </View>
          </View>
        </View>
        <View style={styles.heading}>
          <Text style={styles.headingText}>Popular Restaurants</Text>
          <TouchableWithoutFeedback>
            <Text style={styles.headingLink}>See All</Text>
          </TouchableWithoutFeedback>
        </View>
        <FlatList
          style={styles.popularRestaurant}
          horizontal={true}
          showsHorizontalScrollIndicator={false}
          data={data}
          renderItem={renderItem}
        />
      </ScrollView>
      <Navbar navigation={navigation} />
    </>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 10,
  },
  address: {
    flexDirection: "row",
  },
  search: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 20,
    borderWidth: 1,
    borderRadius: 10,
    borderColor: "#dadae8",
    paddingVertical: 5,
    paddingHorizontal: 5,
  },
  input: {
    color: "#000",
    flex: 1,
  },
  nearbyPlace: {
    marginBottom: 10,
  },
  heading: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 20,
  },
  headingText: {
    fontSize: 16,
  },
  headingLink: {
    fontSize: 13,
    color: globalStyles.color.button,
  },
  nearbyPlaceItem: {
    padding: 5,
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#E5E5E5",
    borderRadius: 10,
    marginBottom: 10,
  },
});
