import { Entypo, EvilIcons, Ionicons } from "@expo/vector-icons";
import React, { useState } from "react";
import { Image, StyleSheet, Text, TextInput, View } from "react-native";
const HomeScreen = () => {
  const [search, setSearch] = useState("");

  return (
    <View>
      <View style={styles.header}>
        <Entypo name="menu" size={25} color="black" />
        <View style={styles.address}>
          <Entypo name="location-pin" size={15} color="black" />
          <Text>Address</Text>
        </View>
        <Image
          source={{ uri: "https://picsum.photos/200", width: 35, height: 35 }}
          style={{ borderRadius: 35 / 2 }}
        />
      </View>
      <View style={styles.search}>
        <EvilIcons
          name="search"
          size={20}
          color="black"
          style={{ marginRight: 5 }}
        />
        <TextInput
          placeholder="Find your food"
          style={styles.input}
          onChangeText={(value) => setSearch(value)}
        />
        <Ionicons
          name="filter-outline"
          size={20}
          color="black"
          style={{ marginLeft: "auto" }}
        />
      </View>
    </View>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({
  header: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    paddingHorizontal: 10,
    paddingVertical: 5,
  },
  address: {
    flexDirection: "row",
  },
  search: {
    flexDirection: "row",
    alignItems: "center",
    // backgroundColor: "red",
    width: "90%",
    marginHorizontal: "auto",
    marginTop: 20,
    borderWidth: 1,
    borderRadius: 10,
    borderColor: "#dadae8",
    paddingVertical: 5,
    paddingHorizontal: 5,
  },
  input: {
    width: "100%",
    // backgroundColor: "#F8F8F8",
    color: "#000",
    // borderWidth: 1,
    // borderRadius: 10,
    // borderColor: "#dadae8",
    // padding: 10,
    // marginBottom: 10,
  },
});
